import React, { useState, useEffect } from 'react';
import Cargando from "../components/Cargando";
import viajesService from "./../services/viajes";
import IndividualesViajes from "../components/IndividualesViajes";
import { FaBus } from 'react-icons/fa';
import { ImOffice } from "react-icons/im";
import { BsCalendar } from "react-icons/bs";
import TextField from '@material-ui/core/TextField';




function ViajesUsuarios(props) {
    const [viajes, setViajes] = useState([]);
    const [cargando, setCargando] = useState(true);   
    const [search, setSearch] = useState("");


    useEffect(() => {
        (async () => {
            const data = await viajesService.index();
            setViajes(data);
            setCargando(false);
        })().catch(err => console.log("Error al traer los viajes: ", err));
    }, []); 

    return (<div >
        <h1 className="mb-5 viajes">Viajes</h1>
        
        <form className="busqueda">
          <div class="form-group ">
            <label for="destino" className="labelcolor"><i className="mr-2"><FaBus style={{ fontSize: 28 }}/></i>Destino</label>
            <input
            className="form-control"
            type="text"
            placeholder="Buscar por destino"
            onChange={(e) => setSearch(e.target.value)}
          />
          </div>

          <div class="form-group">
            <label for="empresa" className="labelcolor"><i className="mr-2"><ImOffice style={{ fontSize: 28 }}/></i>Empresa</label>
            <input
            className="form-control"
            type="text"
            placeholder="Buscar por empresa"
            onChange={(e) => setSearch(e.target.value)}
          />
         </div>
         
         <div class="form-group">
            <label for="salida" className="labelcolor"><i className="mr-2"><BsCalendar style={{ fontSize: 28 }}/></i>Fecha de salida</label>
            <TextField
                        id="hora_salida"
                        type="date"
                        placeholder="Buscar por fecha de salida"
                        className="form-control"
                        format="d-m-Y"
                        onChange={(e) => setSearch(e.target.value)}
                   />
          </div>
  
        </form>
  
{
            cargando ?
            <Cargando/> :
            viajes.filter((lista) =>
            lista.parada_origen.nombre.toLowerCase().includes(search.toLowerCase())).map(item => 
            <div key={item.id_viaje}> <IndividualesViajes viaje={item} /> 
             </div>)}

            

    </div>);
}

export default ViajesUsuarios;
