import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-bootstrap';
import Button from 'react-bootstrap/Button'/* 
import asientosService from "./../services/asientos"; */
import {useHistory, useLocation} from "react-router-dom";
import Modal from 'react-bootstrap/Modal';
import { GiSteeringWheel } from 'react-icons/gi';
import viajesService from '../services/viajes';
import reservasService from '../services/compras';
import Asientos from "./../components/Asientos";



function ElegirAsiento() {
    const location = useLocation()
    const [show, setShow] = useState(false);

    const history = useHistory();   
    const [viajes, setViajes] = useState([]);
    const [reservados, setReservados] = useState([]);

    const [select, setSelect] = useState({});
  
       useEffect(() => {
                (async () => {
                    const data = await viajesService.index();
                    setViajes(data);
                })().catch(err => console.log("Error al traer los viajes: ", err));
            }, []);

          useEffect(() => {
              if(viajes.length>0){
                (async () => {
                  const data = await reservasService.reservasViajes(viajes[0].id);
                  setReservados(data);
              })().catch(err => console.log("Error al traer las reservas: ", err));
              }
          }, [viajes, location.state.id]);       

            const lista = viajes.map(item => ( <span key={item.id}>       
                
                        <Button 
                        className="verde"
                        variant="success">{item.cantidad_asientos} 
                        </Button>                                          
                
             </span>));
            


    return (<div className='pt-3 mb-5'>
        <h1 className="text-center mt-5 mb-5 nuevo"><b>Eleg√≠ tu asiento para viajar</b></h1>
        
          <span className="disp">Disponible</span>
          <span className="ocupado">Ocupado</span>

          
        
         <div className="container asientos-borde">
        <Button className="conductor" disabled="true" variant="info"> <GiSteeringWheel size={40}/></Button>
             {lista}
             <Asientos reservados = {reservados}/>        
        </div> 

    </div>);
}

export default ElegirAsiento;
