import React, { useState, useEffect } from 'react';
import Cargando from "../components/Cargando";
import viajesService from "./../services/viajes";
import IndividualesViajes from "../components/IndividualesViajes";
import { FaBus } from 'react-icons/fa';
import { ImOffice } from "react-icons/im";
import { BsCalendar } from "react-icons/bs";
import TextField from '@material-ui/core/TextField';




function ViajesUsuarios(props) {
    const [viajes, setViajes] = useState([]);
    const [cargando, setCargando] = useState(true);   
    const [search, setSearch] = useState("");


    useEffect(() => {
        (async () => {
            const data = await viajesService.index();
            setViajes(data);
            setCargando(false);
        })().catch(err => console.log("Error al traer los viajes: ", err));
    }, []); 

    return (<div >
        <h1 className="mb-5 viajes">Viajes</h1>
         
  
{
            cargando ?
            <Cargando/> :
            viajes.filter((lista) =>
            lista.nombre.toLowerCase().includes(search.toLowerCase())).map(item => 
            <div key={item.id_viaje}> <IndividualesViajes viaje={item} /> 
             </div>)}

             {cargando ?
                <Cargando/> :
                (<ul className="container-fluid">

                  {viajes.map(empresa => (
                    <li className="mb-4 imagenes" key={empresa.id}>
                        <h2 className="mb-4 font">{empresa.nombre}</h2>
                        <img className="imagen"  src={urlbase + empresa.logo} alt={empresa.id} /> 
                        <p className="informacion">{empresa.informacion}</p>
                    </li>
                    
                    ))}

                </ul>)}  
            

    </div>);
}

export default ViajesUsuarios;
