import React, { useState, useEffect } from 'react';
import Cargando from "../components/Cargando";
import viajesService from "./../services/viajes";
import { Link } from "react-router-dom";/* 
import PanelViajes from "../components/PanelViajes"; */
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import {useHistory} from "react-router-dom";
import { ImOffice } from "react-icons/im";
import Table from 'react-bootstrap/Table';




function Viajes(props) {
    
    const history = useHistory();
    const [viajes, setViajes] = useState([]);
    const [cargando, setCargando] = useState(true);  
    const [search, setSearch] = useState("");
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    useEffect(() => {
        (async () => {
            const data = await viajesService.indexEmpresa();
            setViajes(data);
            setCargando(false);
        })().catch(err => console.log("Error al traer los viajes: ", err));
    }, []); 

    return (<div className="fondopantalla">
        <h1 className="mb-5 viajes-empresa">Panel de viajes</h1>
    <div >
        <Link className="btn btn-primary mb-5 text-center boton" to="/viajes/nueva">Crear nuevo viaje</Link>

        <form className="busqueda mb-5">
            <div class="form-group ">
            <label for="destino" className="labelcolor"><i className="mr-2 "><ImOffice style={{ fontSize: 28 }}/></i>Empresa</label>
                <input
                    className="d-flex mb-3 mt-3 input-empresa"
                    type="text"
                    placeholder="Buscar por empresa"
                    onChange={(e) => setSearch(e.target.value)}
                />
            </div>
         </form>

      
            
             {cargando ?
                <Cargando/> :
                (<div className="container-fluid">

                  {viajes.filter((lista) =>
                  lista.empresa.nombre.toLowerCase().includes(search.toLowerCase())).map(viaje => (<>
                        <Table  variant="warning" striped bordered hover   >
                        <thead key={viaje.id}>
                        <tr className="row">
                        <th className="col-4 text-center colorth">Salida</th>
                        <th className="col-4 text-center colorth">Llegada</th>
                        <th className="col-4 text-center colorth">Precio</th>
                        </tr>
                        </thead>
                        <tbody>
                        <tr className="row"> 
                        <td className="col-4 text-center colortd">{viaje.salida.nombre}</td>
                        <td className="col-4 text-center colortd">{viaje.destino.nombre}</td>
                        <td className="col-4 text-center colortd">$ {viaje.precio}</td>
                        </tr>
                        </tbody>
                        </Table>

                        <Table  variant="warning" striped bordered hover >
                        <thead>
                        <tr className="row">
                        <th className="col-4 text-center colorth">Empresa</th>
                        <th className="col-4 text-center colorth">Fecha  salida</th>
                        <th className="col-4 text-center colorth">Fecha llegada</th>
                        </tr>
                        </thead>
                        <tbody>
                        <tr className="row">
                        <td className="col-4 text-center colortd">{viaje.empresa.nombre}</td>
                        <td className="col-4 text-center colortd">{viaje.fecha_salida}</td>
                        <td className="col-4 text-center colortd ">{viaje.fecha_llegada}</td>
                        </tr>
                        </tbody>
                        </Table>
                    </>
                    ))}

                </div>)}      
            


            <Button variant="primary" onClick={handleShow} className="btn btn-danger mb-2 boton-eliminar-panel" >
                Eliminar
            </Button>


