import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button'
import { NavLink, Link } from 'react-router-dom';
import {useHistory} from "react-router-dom";
import Modal from 'react-bootstrap/Modal';

function ViajesIndividual({ viaje }) {

    function handleClick(e) {    
        e.preventDefault();    
        history.push('/carrito');  }

        
    const history = useHistory();
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    
    function simulateNetworkRequest() {
        return new Promise((resolve) => setTimeout(resolve, 2000));
      }
      

    const [isLoading, setLoading] = useState(false);

    useEffect(() => {
        if (isLoading) {
        simulateNetworkRequest().then(() => {
            setLoading(false);
        });
        }
    }, [isLoading]);


    return (
    
    <li className="listadoviajes bordes mb-4">
        
        <h2 className="nombreviaje white">
            <span><b>{viaje.nombre}</b></span>
        </h2>
        <span className="salida"><b>Salida: </b> {viaje.parada.nombre} {viaje.fecha_salida} {viaje.hora_salida}</span>
        <span className="llegada"><b>Llegada: </b><p>{viaje.parada_origen.nombre} {viaje.fecha_llegada} {viaje.hora_llegada}</p></span>
        <span className="precio"><b>Precio: $ </b> {viaje.precio}</span>
        <span className="empresa"><b>Empresa: </b>{viaje.empresa.nombre}</span>
                  
 <div>
      <>
      <Button className="comprar" variant="primary" onClick={handleShow}>
        Reservar
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>{viaje.nombre} ${viaje.precio}</Modal.Title>
        </Modal.Header>
        <Modal.Body><b>Salida:</b> {viaje.fecha_salida} {viaje.hora_salida}<br></br><br></br> <b>Llegada:</b> {viaje.fecha_llegada} {viaje.hora_llegada}<br></br><br></br><b>Empresa: </b>{viaje.empresa.nombre}<br></br><br></br>
        ¿Estás seguro que queres reservar este pasaje?</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Cancelar
          </Button>
          <Button variant="primary" onClick={handleClick}>
            Reservar
          </Button>
        </Modal.Footer>
      </Modal>
    </>
 </div>

    </li>);
}

export default ViajesIndividual;
