import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button'
import asientosService from "./../services/asientos";
import {useHistory} from "react-router-dom";
import Modal from 'react-bootstrap/Modal';
import { GiSteeringWheel } from 'react-icons/gi';



function ElegirAsiento() {

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const history = useHistory();   
    
   /*  
    const handleClick = ev => {
        ev.preventDefault();  

        fetch('http://127.0.0.1:8000/api/compra/', {
            method: 'post',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                'X-Requested-With': 'XMLHttpRequest',
            },
            credentials: 'include',
            body: JSON.stringify(select),
        });

         history.push('/checkout');  
   
    } */
    const [asientos, setAsientos] = useState([]);
    const [select, setSelect] = useState({});
    


     /*   useEffect(() => {
                (async () => {
                    const data = await asientosService.traerAsientos();
                    setAsientos(data);
                })().catch(err => console.log("Error al traer los asientos: ", err));
            }, []); 
 */
             

          console.log(compras); 

            const lista = asientos.map(item => ( <span key={item.id_asiento}>
              {
                    item.id_estado == 1 ? 
                        (<> 
                        <Button onClick={ () => setSelect(item) } 
                        className="amarillos"
                        variant="warning">{item.id_asiento}</Button>
                       </>) :

                         (<>
                          <Button onClick={ () => setSelect(item) } 
                        className="amarillos"    
                        disabled='true'
                        variant="danger">{item.id_asiento}</Button>
                        </>)
                      
                } 
             </span>));

    return (<div className='pt-3 mb-5'>
        <h1 className="text-center mt-5 mb-5 nuevo"><b>Elegí tu asiento para viajar</b></h1>
        
          <span className="disp">Disponible</span>
          <span className="ocupado">Ocupado</span>
        
        <div className="container asientos-borde">
        <Button className="conductor" disabled="true" variant="info"> <GiSteeringWheel size={40}/></Button>
             {lista}  
                
  </div>

  <Button onClick={handleShow} className="btn btn-primary carrito-boton">Reservar</Button>

  <>
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
        </Modal.Header>
        <Modal.Body>¿Estás seguro que deseas elegir este asiento?</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Cerrar
          </Button>
          <Button variant="primary" onClick={handleClick} >
          Reservar
          </Button>
        </Modal.Footer>
      </Modal>
    </> 
    </div>);
}

export default ElegirAsiento;
