import React, { useState, useEffect, useContext } from 'react';
import empresasService from "../services/empresas";
import Cargando from "../components/Cargando";
import { Link, NavLink } from "react-router-dom";
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';


function EmpresasUsuarios(props) {

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const urlbase = "http://localhost:8000/imgs/empresas/logos/" ;
    const [empresas, setEmpresas] = useState([]);
    const [cargando, setCargando] = useState(true);

    useEffect(() => {
        (async () => {
            const data = await empresasService.traerEmpresas();
            setEmpresas(data);
            setCargando(false);
        })().catch(err => console.error('Error al traer las empresas: ', err));
    }, []);

    const lista = empresas.map(item => (
    <li className="mb-4 imagenes" key={item.id_empresa}>
        
        <h1 className="mb-4 font">{item.nombre}</h1>
       
       <img className="imagen"  src={urlbase + item.logo} alt={item.id_empresa} /> 

    <p className="informacion">{item.informacion}</p>

    </li>
    
    ));

    return (<div className=" pt-3">
        
        {cargando ?
            <Cargando/> :
            (<ul className="container-fluid">
                {lista}
            </ul>)}

            
    </div>);
}

export default EmpresasUsuarios;
