import React, {useState, useContext, useEffect} from 'react';
import authService, {AuthContext} from "../services/auth";
import reservasService from "./../services/reservas";
import Table from 'react-bootstrap/Table';
import tevez from '../img/tevez.png';
import {format} from 'date-fns';

function Perfil() {

    const [reservas, setReservas] = useState([]);

    useEffect(() => {
        (async () => {
            const data = await reservasService.index();
            setReservas(data);
        })().catch(err => console.log("Error al traer las reservas: ", err));
    }, []);
    
    const lista = reservas && reservas.map(reserva => ( <div key={reserva.id}>
       

            <Table  variant="warning" striped bordered hover   >
                <thead>
                    <tr className="row">
                    <th className="col-4 text-center colorth">Nombre</th>
                    <th className="col-4 text-center colorth">Nro de asientos</th>
                    <th className="col-4 text-center colorth">Comodidad</th>
                    </tr>
                </thead>
                <tbody>
                    <tr className="row"> 
                    <td className="col-4 text-center colortd">{reserva.user.name}</td>
                    <td className="col-4 text-center colortd">{format(new Date(reserva.viaje.fecha_salida), 'dd-MM-yyyy hh:mm:ss a')}</td>
                    </tr>
                </tbody>
            </Table>

            <Table  variant="warning" striped bordered hover   >
                <thead>
                    <tr className="row">
                    <th className="col-4 text-center colorth">Destino</th>
                    <th className="col-4 text-center colorth">Salida</th>
                    <th className="col-4 text-center colorth">Llegada</th>
                    </tr>
                </thead>
                <tbody>
                    <tr className="row"> 
                    <td className="col-4 text-center colortd">Bs.As. a Sta. Teresita</td>
                    <td className="col-4 text-center colortd">07-01-2021 18:00:00</td>
                    <td className="col-4 text-center colortd">07-01-2021 22:00:00</td>
                    </tr>
                </tbody>
            </Table>

            <hr className="perfileshr" />
        
            </div> ));

        

    const authData = useContext(AuthContext);

    return (<div className='container pt-3 '>
        <h1 className="viajes mb-5">Perfil</h1>
        <div className="caja-perfil">
        <h2 className="text-center mt-5"><img
          src={tevez}
          alt="tevez"
          className="tevez"
        /></h2>
        <p className="text-center mb-5 mt-4 email"><b>Email :</b>  {authData.user.email}</p>
        </div>
        <h3 className="text-center mb-5 pasajes"><b>Pasajes Reservados</b></h3>
            {lista}
        </div>);
}

export default Perfil;
