import React, { useState, useEffect } from 'react';
import Cargando from "../components/Cargando";
import viajesService from "./../services/viajes";
import { Link } from "react-router-dom";/* 
import PanelViajes from "../components/PanelViajes"; */
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import {useHistory} from "react-router-dom";
import { ImOffice } from "react-icons/im";




function Viajes(props) {
    
    const history = useHistory();
    const [viajes, setViajes] = useState([]);
    const [cargando, setCargando] = useState(true);  
    const [search, setSearch] = useState("");
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    useEffect(() => {
        (async () => {
            const data = await viajesService.indexEmpresa();
            setViajes(data);
            setCargando(false);
        })().catch(err => console.log("Error al traer los viajes: ", err));
    }, []); 

    return (<div>
        <h1 className="mb-5 viajes-empresa">Panel de viajes</h1>
    <div >
        <Link className="btn btn-primary mb-5 text-center boton" to="/viajes/nueva">Crear nuevo viaje</Link>

        <form className="busqueda mb-5">
            <div class="form-group ">
            <label for="destino" className="labelcolor"><i className="mr-2 "><ImOffice style={{ fontSize: 28 }}/></i>Empresa</label>
                <input
                    className="d-flex mb-3 mt-3 input-empresa"
                    type="text"
                    placeholder="Buscar por empresa"
                    onChange={(e) => setSearch(e.target.value)}
                />
            </div>
         </form>

        {   
            cargando ?
            <Cargando/> :
            viajes.filter((lista) =>
            lista.empresa.nombre.toLowerCase().includes(search.toLowerCase())).map(item => <div key={item.id}> 
            
             {cargando ?
                <Cargando/> :
                (<ul className="container-fluid">

                  {viajes.filter((lista) =>
                  lista.empresa.nombre.toLowerCase().includes(search.toLowerCase())).map(viaje => (
                    <li className="listadoviajes bordes mb-4" key={viaje.id}>
                        <h2 className="nombreviaje white">{viaje.salida.nombre} a {viaje.destino.nombre}
                        </h2>
                        <span className="salida"> <b className="mr-1">Empresa:</b> {viaje.empresa.nombre}</span>
                        <span className="llegada"> <b className="mr-1">Hora salida:</b> {viaje.fecha_salida}</span>
                        <span className="llegada"> <b className="mr-1">Hora llegada:</b> {viaje.fecha_llegada}</span>
                        <span className="precio"><b className="mr-1">Precio: $ </b> {viaje.precio}</span>
                        <span className="precio"><b className="mr-1">Asientos:</b> {viaje.cantidad_asientos}</span>
                    </li>
                    
                    ))}

                </ul>)}      
            


            <Button variant="primary" onClick={handleShow} className="btn btn-danger mb-2 boton-eliminar-panel" >
                Eliminar
            </Button>

            <hr className="hr" />


    <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
        </Modal.Header>
        <Modal.Body>¿Estás seguro que deseas eliminar este viaje?</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
              <button className="btn btn-secondary">
                 Cerrar
              </button>
          </Button>
          <Button onClick={handleClose}>
          <button className="btn btn-primary" 
            onClick={() => {
                viajesService.eliminarViaje(item.id)
                    .then(data => {
                        setViajes(viajes.filter(viaje => viaje.id !== item.id));
                        if(typeof props.notExitosaEliminar === 'function') {
                            props.notExitosaEliminar(data);
                            
                        }
                        history.push('/'); 
                    })
                    
                    .catch(err => {
                        if(typeof props.notDenegadaEliminar === 'function') {
                            props.notDenegadaEliminar(item);
                        }
                    });
             }}>Eliminar</button>
          </Button>
        </Modal.Footer>
      </Modal>
      
            </div>)}
    </div> </div>);
}

export default Viajes;
