import {API, FETCH_HEADERS} from "./../constants";

let viajes = [];

const viajesService = {
    
    async traerViajes() {
      
        const fetchRes = await fetch(API + '/viajes', {
            headers: {
                'Content-Type': 'application/json'
            },
            credentials: 'include'
        });
        const response = await fetchRes.json();
        viajes = response.data;
        return viajes;
    },


    async traerViajesX() {
      
        const fetchRes = await fetch(API + '/viajess', {
            headers: {
                'Content-Type': 'application/json'
            },
            credentials: 'include'
        });
        const response = await fetchRes.json();
        viajes = response.data;
        return viajes;
    },
    
    async crearViaje(data) {
        const response = await fetch(API + '/viajes', {
            method: 'POST',
            body: JSON.stringify(data),
            headers: {
                'Content-Type': 'application/json',
               
                'X-Requested-With': 'XMLHttpRequest'
            }
        });
        const fetchData = await response.json();
        if(!fetchData.success) {
            return {...fetchData};
        }
        return {...fetchData.data};
    },


    async eliminarViaje(id) {
        const response = await fetch(API + '/viajes/' + id, {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json',
               
                'X-Requested-With': 'XMLHttpRequest'
            }
        });
        const fetchData = await response.json();
        if(!fetchData.success) {
            throw new Error('Error al eliminar el viaje :(');
        }
        return {...fetchData.data};
    }

};

export default viajesService;
