import React, { useState, useEffect } from 'react';
import Cargando from "../components/Cargando";
import viajesService from "./../services/viajes";
import IndividualesViajes from "../components/IndividualesViajes";
import { FaBus } from 'react-icons/fa';
import { ImOffice } from "react-icons/im";
import { BsCalendar } from "react-icons/bs";
import TextField from '@material-ui/core/TextField';




function ViajesUsuarios() {
    const [viajes, setViajes] = useState([]);
    const [cargando, setCargando] = useState(true); 


    useEffect(() => {
        (async () => {
            const data = await viajesService.index();
            setViajes(data);
            setCargando(false);
        })().catch(err => console.log("Error al traer los viajes: ", err));
    }, []); 

    return (<div >
        <h1 className="mb-5 viajes">Viajes</h1>
         

             {cargando ?
                <Cargando/> :
                (<ul className="container-fluid">

                  {viajes.map(viaje => (
                    <li className="mb-4 imagenes" key={viaje.id}>
                        <h2 className="mb-4 font">{viaje.precio}</h2>
                        <span className="salida"><b>Salida: </b> {viaje.parada.nombre} {viaje.fecha_salida} {viaje.hora_salida}</span>
                        <span className="llegada"><b>Llegada: </b><p>{viaje.parada_origen.nombre} {viaje.fecha_llegada} {viaje.hora_llegada}</p></span>
                        <span className="precio"><b>Precio: $ </b> {viaje.precio}</span>
                        <span className="empresa"><b>Empresa: </b>{viaje.empresa.nombre}</span>
                    </li>
                    
                    ))}

                </ul>)}  
            

    </div>);
}

export default ViajesUsuarios;
